import React from 'react'
import Description from './Description';


const Movie = (props) => {


  const showDescription = function(){
    // const description = document.querySelectorAll(".movie-description");
    // const desc = document.querySelector("Description");
    // desc[props.id].style.display = "block";
  }
  const closeDescription = function(){
    // const description = document.querySelectorAll(".movie-description");
    const desc = document.querySelector("Description");
    desc[props.id].style.display = "none";
  }

  return (
    <>
        <div className="movie-card" onMouseOver={showDescription} onMouseOut={closeDescription}>
            <div className="movie-image">
                <img src={props.poster_path} alt="poster"/>
            </div>
            <div className="movie-title">
                <h3>{props.title}</h3>
                <span>{props.vote_average}</span>
            </div>

            <Description title={props.title} overview={props.overview}/>

        </div>
    </>
  )
}

export default Movie
