import React, { useState } from 'react'

import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');


    const handleLogin = () =>{
        const emailForm = /^([0-9a-zA-Z_\.-]+)@([0-9a-zA-Z_-]+)(\.[0-9a-zA-Z_-]+){1,2}$/;

        if(!emailForm.test(email)){
            alert[2].textContent = "올바른 메일 형식이 아닙니다!";
            canSignUp = false;
            alert[2].style.color = "red";
        }else{
            alert[2].textContent = "올바른 메일 형식입니다!";
            canSignUp = true;
            alert[2].style.color = "lightgreen";
        }
    }

  return (
    <div className='login-page'>
        <div className="login-container">
            <h2>이메일과 비밀번호를<br />입력해주세요</h2>

            <form action="" id='loginForm'>
                <label>이메일주소 <br />
                    <input type="email" id='email' onChange={(e)=> setEmail(e.target.value)}/>
                </label>
                <br />
                <label>비밀번호 <br />
                    <input type="password" id="password" placeholder='영문, 숫자, 특수문자 포함 8자 이상' onChange={(e)=> setPassword(e.target.value)}/>
                </label>
                <br />
                <input type="button" value="확인" id="loginBtn" className='' onClick={()=>navigate('/')}/>
            </form>
        </div>
    </div>
  )
}

export default Login
